<?php

namespace Database\Factories;

use Illuminate\Database\Eloquent\Factories\Factory;
use PhpOffice\PhpWord\IOFactory;
use Illuminate\Support\Facades\Storage;
use App\Models\Book;
use App\Models\Category;
/**
 * @extends \Illuminate\Database\Eloquent\Factories\Factory<\App\Models\Model>
 */

class BookFactory extends Factory
{
    /**
     * Define the model's default state.
     *
     * @return array<string, mixed>
     */
    public function definition(): array
    {
        ini_set('memory_limit', '512M');

        $category_ids = Category::pluck('id')->toArray();
        $cover_image = "";

        $filePath = storage_path('app/public/content.txt');
        $content = $this->parseFileToBase64($filePath);

        return [
            'title' => $this->faker->name(),
            'author' => $this->faker->name(),
            'category_id' => $this->faker->randomElement($category_ids),
            'price' => $this->faker->randomFloat(2, 1, 10000),
            'description' => $this->faker->text(),
            'cover_image' => $cover_image,
            'content' => $content,
            'discount' => $this->faker->numberBetween(0, 100),
            'is_featured' => $this->faker->numberBetween(0, 1),
            'is_vip_valid' => $this->faker->numberBetween(0, 1),
            'status' => $this->faker->numberBetween(1, 10),
            'created_at' => $this->faker->dateTimeBetween('-30 days', 'now'),
            'updated_at' => $this->faker->dateTime(),
        ];

    }

    private function parseFileToBase64(string $filePath)
    {
        $data = file_get_contents($filePath);
        $base64 = base64_encode($data);
        return $base64;
    }
}
